                this.showModal = true;
                this.generatePdf();
            },
            generatePdf() {
                let element = document.getElementById('resume');
                // html2pdf(element);
                html2pdf()
                    .from(element)
                    .set({
                        margin:       [30, 0, 130, 0],
                        filename:     'file.pdf',
                        image:        { type: 'jpeg',quality: 0.98 },
                        html2canvas:  { dpi: 96, scale: 1, scrollX: 0, scrollY: 0, backgroundColor: '#FFF' },
                        jsPDF:        { unit: 'pt', format: 'a4', orientation: 'p' },
                        pagebreak: { before: '.page-break', avoid: 'img' }
                    })
                    .toPdf()
                    .get('pdf').then(function (pdf) {
                    var totalPages = pdf.internal.getNumberOfPages();

                    for (let i = 1; i <= totalPages; i++) {
                        pdf.setPage(i);
                        pdf.setFontSize(10);
                        pdf.setTextColor(150);
                        pdf.text( 'Chaser IT Search', pdf.internal.pageSize.getWidth() - 550, pdf.internal.pageSize.getHeight() - 50);
                        pdf.text( 'E-mail: team@chaser.one', pdf.internal.pageSize.getWidth() - 550, pdf.internal.pageSize.getHeight() - 40);
